import { FORM_PROJECT, GET_PROJECTS } from '../../types';

export default (state, action) => {
    switch (action.type) {
        case FORM_PROJECT:
            return {
                ...state,
                formNewProject: true
            }
        case GET_PROJECTS:
            return {
                ...state,
                formNewProject: true
            }
        default:
            return state;
    }
}
